import { useState } from 'react';
import './App.css';
import './css/bulma.min.css';


// import the library
import { library } from '@fortawesome/fontawesome-svg-core'

// import your icons
import { fas } from '@fortawesome/free-solid-svg-icons'
import { far } from '@fortawesome/free-regular-svg-icons'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'



function App() {

  const [todoList, setTodoList] = useState([]);
  const [newTask, setNewTask] = useState("");

  const handleChange = (event) => {
    setNewTask (event.target.value);
  };
  const addTask = (event) => {
    const task = {
      id: todoList.length === 0 ? 1 : todoList[todoList.lenght-1].id+1,
      taskName: newTask
    }
    if (event.key === "Enter") {
      setTodoList([...todoList,newTask]);
      document.querySelector('#todo-input').value="";
    }
  };
  const deleteTask = (id) =>{
    setTodoList(todoList.filter((task)=> task !== id));
  };
  const completeTask = (taskName) =>{
      const newTodoList = todoList.filter((task)=>{
        return task !== taskName;
      });
      setTodoList(newTodoList);
  };

  return (
    <div className="App">
      <div className="container">
        <div className="columns">
          <div className="column"></div>
          <div className="column">
              <nav className="panel">
                <p className="panel-heading">TO DO</p>
                <div className="panel-block">
                  <p className="control has-icons-left">
                    <input id="todo-input" className="input" type="text" placeholder="Add a task" onChange={handleChange} onKeyDown={addTask}/>
                    <span className="icon is-left">
                      <i className="fas fa-search" aria-hidden="true"></i>
                    </span>
                  </p>
                </div>
                {todoList.map((task)=>{
                  return <div className="panel-block is-flex iis-done" key={task.id}>
                  <span>&nbsp;</span>
                  <a className="todo-icon remove" onClick={() =>deleteTask(task.id)}>
                    <FontAwesomeIcon icon="fa-solid fa-minus" />
                  </a>
                  <div className="todo-task">{task.taskName }</div>
                  <a className="todo-icon check" onClick={() =>comlpleteTask(task.id)}>
                    <FontAwesomeIcon icon="fa-solid fa-check" />
                  </a>
                </div>
                })}


                <div className="panel-block reset">
                  <button className="button is-link is-outlined is-fullwidth">
                    Reset list
                  </button>
                </div>
              </nav>
          </div>
          <div className="column"></div>
        </div>
      </div>
    </div>
  );
}

export default App;
library.add(fas, far)
