import { useState } from 'react';
import './App.css';
import './css/bulma.min.css'; 

function App() {

  const [ countValue, setCountValue  ] = useState(0);

  const increaseValue = () => {setCountValue(countValue + 1)};
  const decreaseValue = () => {setCountValue(countValue - 1)};
  const resetValue = () => {setCountValue(0)};

  return (
    <div className="App">
      <div class="container">
        <div class="column"></div>
        <div class="column">
            <div className="box">I'm in a box.</div>
        </div>
        <div class="column"></div>
      </div>

      <button onClick={increaseValue}>Increase</button>
      <button onClick={decreaseValue}>Decrease</button>
      <button onClick={resetValue}>Set to Zero</button>

      {countValue}
      </div>
  );
}

export default App;
