import { useState } from 'react';
import './App.css';

function App() {

  const [ countValue, setCountValue  ] = useState(2);

  const increaseValue = setCountValue(countValue ++);
  const decreaseValue = setCountValue(countValue --);
  const resetValue = setCountValue(0);

  return (
    <div className="App">
      <button onClick={()=>{increaseValue()}}>Increase</button>
      <button>Decrease</button>
      <button>Set to Zero</button>

      {countValue}
      </div>
  );
}

export default App;
