export const Task = (props) => {
  return (
    <div className={"panel-block is-flex" + props.status} key={props.id}>
      <span>&nbsp;</span>
      <a className="todo-icon remove" onClick={() => deleteTask(props.id)}>
        <FontAwesomeIcon icon="fa-solid fa-minus" />
      </a>
      <div className="todo-task">{props.taskName}</div>
      <a className="todo-icon check" onClick={() => completeTask(props.id)}>
        <FontAwesomeIcon icon="fa-solid fa-check" />
      </a>
    </div>
  );
};
